// Extract docs/*.md files from commits
const payload = $input.first().json.body || $input.first().json;
const commits = payload.commits || [];
const repo = payload.repository;

const docFiles = [];
const deletedFiles = [];

for (const commit of commits) {
  const addedModified = [...(commit.added || []), ...(commit.modified || [])];
  for (const file of addedModified) {
    if (file.startsWith('docs/') && file.endsWith('.md')) {
      docFiles.push({
        path: file,
        repo: repo.full_name,
        owner: repo.owner.login || repo.owner.name,
        repoName: repo.name,
        action: 'upsert'
      });
    }
  }

  for (const file of (commit.removed || [])) {
    if (file.startsWith('docs/') && file.endsWith('.md')) {
      deletedFiles.push({
        path: file,
        repo: repo.full_name,
        owner: repo.owner.login || repo.owner.name,
        repoName: repo.name,
        action: 'delete'
      });
    }
  }
}

const seen = new Set();
const uniqueFiles = [...docFiles, ...deletedFiles].filter(f => {
  const key = f.repo + ':' + f.path;
  if (seen.has(key)) return false;
  seen.add(key);
  return true;
});

return uniqueFiles.map(f => ({ json: f }));

// Process ALL input items, not just the first one
const inputItems = $input.all();
const allFileData = $('Extract Doc Files').all();
const results = [];

// Parse inline markdown to Notion rich_text with proper bold handling
function parseInline(text) {
  const result = [];
  // Replace hyphens with em dashes in separators
  text = text.replace(/ - /g, ' – ');
  let i = 0;
  while (i < text.length) {
    // Bold **text**
    if (text.slice(i, i+2) === '**') {
      const end = text.indexOf('**', i+2);
      if (end !== -1) {
        result.push({ type: 'text', text: { content: text.slice(i+2, end) }, annotations: { bold: true } });
        i = end + 2;
        continue;
      }
    }
    // Code `text`
    if (text[i] === '`') {
      const end = text.indexOf('`', i+1);
      if (end !== -1) {
        result.push({ type: 'text', text: { content: text.slice(i+1, end) }, annotations: { code: true } });
        i = end + 1;
        continue;
      }
    }
    // Link [text](url)
    if (text[i] === '[') {
      const bracketEnd = text.indexOf(']', i);
      if (bracketEnd !== -1 && text[bracketEnd+1] === '(') {
        const parenEnd = text.indexOf(')', bracketEnd);
        if (parenEnd !== -1) {
          const linkText = text.slice(i+1, bracketEnd);
          const url = text.slice(bracketEnd+2, parenEnd);
          result.push({ type: 'text', text: { content: linkText, link: { url } } });
          i = parenEnd + 1;
          continue;
        }
      }
    }
    // Collect plain text until next special char
    let nextSpecial = text.length;
    for (const char of ['**', '`', '[']) {
      const pos = text.indexOf(char, i);
      if (pos !== -1 && pos < nextSpecial) nextSpecial = pos;
    }
    if (nextSpecial > i) {
      result.push({ type: 'text', text: { content: text.slice(i, nextSpecial) } });
      i = nextSpecial;
    } else {
      result.push({ type: 'text', text: { content: text[i] } });
      i++;
    }
  }
  // Merge adjacent plain text segments
  const merged = [];
  for (const seg of result) {
    if (merged.length > 0 && !seg.annotations && !merged[merged.length-1].annotations && !seg.text.link && !merged[merged.length-1].text.link) {
      merged[merged.length-1].text.content += seg.text.content;
    } else {
      merged.push(seg);
    }
  }
  return merged.length > 0 ? merged : [{ type: 'text', text: { content: text } }];
}

for (let idx = 0; idx < inputItems.length; idx++) {
  const binaryData = await this.helpers.getBinaryDataBuffer(idx, 'data');
  const content = binaryData.toString('utf-8');
  const fileData = allFileData[idx].json;
  const path = fileData.path;
  const repo = fileData.repo;

  let title = path.split('/').pop().replace('.md', '').replace(/-/g, ' ');
  const headingMatch = content.match(/^#\s+(.+)$/m);
  if (headingMatch) {
    title = headingMatch[1];
  }

  let bodyContent = content;
  const fmMatch = content.match(/^---\n[\s\S]*?\n---\n([\s\S]*)$/);
  if (fmMatch) {
    bodyContent = fmMatch[1];
  }

  // Skip HTML comments and the H1 title line
  const lines = bodyContent.split('\n').filter(line => !line.match(/^<!--.*-->$/) && !line.match(/^#\s/));
  const blocks = [];

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    if (!line.trim()) continue;

    if (line.startsWith('### ')) {
      blocks.push({ object: 'block', type: 'heading_3', heading_3: { rich_text: parseInline(line.slice(4)) } });
    } else if (line.startsWith('## ')) {
      blocks.push({ object: 'block', type: 'heading_2', heading_2: { rich_text: parseInline(line.slice(3)) } });
    } else if (line.startsWith('# ')) {
      blocks.push({ object: 'block', type: 'heading_1', heading_1: { rich_text: parseInline(line.slice(2)) } });
    } else if (line.startsWith('- ') || line.startsWith('* ')) {
      const itemText = line.slice(2);
      // Check if it's a phase item or plan item - use to_do block
      const isPhase = itemText.match(/^\*\*Phase \d+/);
      const isPlan = itemText.match(/^\d{2}-\d{2}:/);
      // Also check for task items like [ ] or [x]
      const isTask = itemText.match(/^\[[ x]\]/);
      if (isPhase || isPlan || isTask) {
        const checked = itemText.includes('✓') || itemText.match(/^\[x\]/i);
        const cleanText = itemText.replace(/^\[[ x]\]\s*/, '');
        blocks.push({ object: 'block', type: 'to_do', to_do: { rich_text: parseInline(cleanText), checked } });
      } else {
        blocks.push({ object: 'block', type: 'bulleted_list_item', bulleted_list_item: { rich_text: parseInline(itemText) } });
      }
    } else if (/^\d+\.\s/.test(line)) {
      blocks.push({ object: 'block', type: 'numbered_list_item', numbered_list_item: { rich_text: parseInline(line.replace(/^\d+\.\s/, '')) } });
    } else if (line.startsWith('```')) {
      const lang = line.slice(3).trim() || 'plain text';
      const codeLines = [];
      i++;
      while (i < lines.length && !lines[i].startsWith('```')) {
        codeLines.push(lines[i]);
        i++;
      }
      blocks.push({ object: 'block', type: 'code', code: { rich_text: [{ type: 'text', text: { content: codeLines.join('\n') } }], language: lang } });
    } else if (line.startsWith('> ')) {
      blocks.push({ object: 'block', type: 'quote', quote: { rich_text: parseInline(line.slice(2)) } });
    } else if (line.startsWith('|')) {
      // Parse markdown table
      const tableLines = [line];
      while (i + 1 < lines.length && lines[i + 1].startsWith('|')) {
        i++;
        tableLines.push(lines[i]);
      }
      // Filter out separator rows (|---|---| or |:---|:---:|)
      const dataRows = tableLines.filter(row => !row.match(/^\|[\s:\-|]+\|$/));
      if (dataRows.length > 0) {
        const parsedRows = dataRows.map(row => {
          const cells = row.split('|').slice(1, -1).map(cell => parseInline(cell.trim()));
          return { type: 'table_row', table_row: { cells } };
        });
        const tableWidth = parsedRows[0]?.table_row.cells.length || 1;
        blocks.push({ object: 'block', type: 'table', table: { table_width: tableWidth, has_column_header: true, has_row_header: false, children: parsedRows } });
      }
    } else {
      blocks.push({ object: 'block', type: 'paragraph', paragraph: { rich_text: parseInline(line) } });
    }
  }

  results.push({
    json: {
      title,
      github_path: repo + ':' + path,
      github_repo: repo,
      blocks: blocks.slice(0, 100),
      action: fileData.action
    }
  });
}

return results;

// Merge query results with markdown data - iterate through all items
const inputItems = $input.all();
const mdDataItems = $('Convert MD to Notion').all();
const results = [];

for (let i = 0; i < inputItems.length; i++) {
  const queryResult = inputItems[i].json;
  const mdData = mdDataItems[i].json;

  if (queryResult.id) {
    results.push({ json: { exists: true, pageId: queryResult.id, ...mdData } });
  } else {
    results.push({ json: { exists: false, ...mdData } });
  }
}

return results;

// Output each block as separate item for deletion - process all items
const inputItems = $input.all();
const mergeItems = $('Merge Decision').all();
const results = [];

for (let i = 0; i < inputItems.length; i++) {
  const blocksResponse = inputItems[i].json;
  const oldBlocks = blocksResponse.results || [];
  const pageId = mergeItems[i].json.pageId;
  const blocks = mergeItems[i].json.blocks;

  if (oldBlocks.length === 0) {
    results.push({ json: { skipDelete: true, pageId, blocks } });
  } else {
    for (const block of oldBlocks) {
      results.push({ json: { blockId: block.id, pageId, blocks } });
    }
  }
}

return results;

// Get all items from Split Blocks to Delete - deduplicate by pageId
const splitItems = $('Split Blocks to Delete').all();

// Deduplicate by pageId (multiple block deletes per page)
const byPage = {};
for (const item of splitItems) {
  const pageId = item.json.pageId;
  const blocks = item.json.blocks;
  if (pageId && !byPage[pageId]) {
    byPage[pageId] = { pageId, blocks };
  }
}

// Return one item per unique page
return Object.values(byPage).map(data => ({ json: data }));
